INSERT INTO `rules_config` VALUES ('21','rules_booking_create_send_email_double','Send email on double booking creation','reaction rule','0','0','3','1','viking_booking_entity','rules','0','O:17:"RulesReactionRule":21:{s:9:"\0*\0parent";N;s:2:"id";s:2:"21";s:12:"\0*\0elementId";N;s:6:"weight";s:1:"0";s:8:"settings";a:0:{}s:4:"name";s:38:"rules_booking_create_send_email_double";s:5:"label";s:37:"Send email on double booking creation";s:6:"plugin";s:13:"reaction rule";s:6:"active";b:0;s:6:"status";i:3;s:5:"dirty";b:0;s:6:"module";s:21:"viking_booking_entity";s:5:"owner";s:5:"rules";s:14:"access_exposed";s:1:"0";s:4:"data";r:1;s:4:"tags";a:0:{}s:11:"\0*\0children";a:1:{i:0;O:11:"RulesAction":6:{s:9:"\0*\0parent";r:1;s:2:"id";N;s:12:"\0*\0elementId";N;s:6:"weight";i:0;s:8:"settings";a:12:{s:3:"key";s:17:"mail-[booking:id]";s:2:"to";s:21:"[booking:field-email]";s:9:"from_mail";s:25:"testgfw@vikingcruises.com";s:7:"subject";s:73:"Bon Voyage [booking:field-first-name] and [booking:passenger-2-firstname]";s:4:"body";s:18771:"<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n<head style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n<!-- If you delete this meta tag, Half Life 3 will never be released. -->\r\n<meta name="viewport" content="width=device-width" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n<title style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">Viking GFW Invite Email</title>\r\n\r\n<style tye="text/css" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n  <!--\r\n  /* ------------------------------------- \r\n    GLOBAL \r\n------------------------------------- */\r\n* { \r\n  margin:0;\r\n  padding:0;\r\n}\r\n* { font-family: "Helvetica Neue", "Helvetica", Helvetica, Arial, sans-serif; }\r\n\r\nimg { \r\n  max-width: 100%; \r\n}\r\n.collapse {\r\n  margin:0;\r\n  padding:0;\r\n}\r\nbody {\r\n  -webkit-font-smoothing:antialiased; \r\n  -webkit-text-size-adjust:none; \r\n  width: 100%!important; \r\n  height: 100%;\r\n}\r\n\r\n\r\n/* ------------------------------------- \r\n    ELEMENTS \r\n------------------------------------- */\r\na { color: #2BA6CB;}\r\n\r\n.btn {\r\n  text-decoration:none;\r\n  color: #FFF;\r\n  background-color: #666;\r\n  padding:10px 16px;\r\n  font-weight:bold;\r\n  margin-right:10px;\r\n  text-align:center;\r\n  cursor:pointer;\r\n  display: inline-block;\r\n}\r\n\r\np.callout {\r\n  padding:15px;\r\n  background-color:#ECF8FF;\r\n  margin-bottom: 15px;\r\n}\r\n.callout a {\r\n  font-weight:bold;\r\n  color: #2BA6CB;\r\n}\r\n\r\ntable.social {\r\n/*  padding:15px; */\r\n  background-color: #ebebeb;\r\n  \r\n}\r\n.social .soc-btn {\r\n  padding: 3px 7px;\r\n  font-size:12px;\r\n  margin-bottom:10px;\r\n  text-decoration:none;\r\n  color: #FFF;font-weight:bold;\r\n  display:block;\r\n  text-align:center;\r\n}\r\na.fb { background-color: #3B5998!important; }\r\na.tw { background-color: #1daced!important; }\r\na.gp { background-color: #DB4A39!important; }\r\na.ms { background-color: #000!important; }\r\n\r\n.sidebar .soc-btn { \r\n  display:block;\r\n  width:100%;\r\n}\r\n\r\n/* ------------------------------------- \r\n    HEADER \r\n------------------------------------- */\r\ntable.head-wrap { width: 100%;}\r\n\r\n.header.container table td.logo { padding: 15px; }\r\n.header.container table td.label { padding: 15px; padding-left:0px;}\r\n\r\n\r\n/* ------------------------------------- \r\n    BODY \r\n------------------------------------- */\r\ntable.body-wrap { width: 100%;}\r\n\r\n\r\n/* ------------------------------------- \r\n    FOOTER \r\n------------------------------------- */\r\ntable.footer-wrap { width: 100%;  clear:both!important;\r\n}\r\n.footer-wrap .container td.content  p { border-top: 1px solid rgb(215,215,215); padding-top:15px;}\r\n.footer-wrap .container td.content p {\r\n  font-size:10px;\r\n  font-weight: bold;\r\n  \r\n}\r\n\r\n\r\n/* ------------------------------------- \r\n    TYPOGRAPHY \r\n------------------------------------- */\r\nh1,h2,h3,h4,h5,h6 {\r\nfont-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif; line-height: 1.1; margin-bottom:15px; color:#000;\r\n}\r\nh1 small, h2 small, h3 small, h4 small, h5 small, h6 small { font-size: 60%; color: #6f6f6f; line-height: 0; text-transform: none; }\r\n\r\nh1 { font-weight:200; font-size: 44px;}\r\nh2 { font-weight:200; font-size: 37px;}\r\nh3 { font-weight:500; font-size: 27px;}\r\nh4 { font-weight:500; font-size: 23px;}\r\nh5 { font-weight:900; font-size: 17px;}\r\nh6 { font-weight:900; font-size: 14px; text-transform: uppercase; color:#444;}\r\n\r\n.collapse { margin:0!important;}\r\n\r\np, ul { \r\n  margin-bottom: 10px; \r\n  font-weight: normal; \r\n  font-size:14px; \r\n  line-height:1.6;\r\n}\r\np.lead { font-size:17px; }\r\np.last { margin-bottom:0px;}\r\n\r\nul li {\r\n  margin-left:5px;\r\n  list-style-position: inside;\r\n}\r\n\r\n/* ------------------------------------- \r\n    SIDEBAR \r\n------------------------------------- */\r\nul.sidebar {\r\n  background:#ebebeb;\r\n  display:block;\r\n  list-style-type: none;\r\n}\r\nul.sidebar li { display: block; margin:0;}\r\nul.sidebar li a {\r\n  text-decoration:none;\r\n  color: #666;\r\n  padding:10px 16px;\r\n/*  font-weight:bold; */\r\n  margin-right:10px;\r\n/*  text-align:center; */\r\n  cursor:pointer;\r\n  border-bottom: 1px solid #777777;\r\n  border-top: 1px solid #FFFFFF;\r\n  display:block;\r\n  margin:0;\r\n}\r\nul.sidebar li a.last { border-bottom-width:0px;}\r\nul.sidebar li a h1,ul.sidebar li a h2,ul.sidebar li a h3,ul.sidebar li a h4,ul.sidebar li a h5,ul.sidebar li a h6,ul.sidebar li a p { margin-bottom:0!important;}\r\n\r\n\r\n\r\n/* --------------------------------------------------- \r\n    RESPONSIVENESS\r\n    Nuke it from orbit. It''s the only way to be sure. \r\n------------------------------------------------------ */\r\n\r\n/* Set a max-width, and make it display as block so it will automatically stretch to that width, but will also shrink down on a phone or something */\r\n.container {\r\n  display:block!important;\r\n  max-width:600px!important;\r\n  margin:0 auto!important; /* makes it centered */\r\n  clear:both!important;\r\n}\r\n\r\n/* This should also be a block element, so that it will fill 100% of the .container */\r\n.content {\r\n  padding:15px;\r\n  max-width:600px;\r\n  margin:0 auto;\r\n  display:block; \r\n}\r\n\r\n/* Let''s make sure tables in the content area are 100% wide */\r\n.content table { width: 100%; }\r\n\r\n\r\n/* Odds and ends */\r\n.column {\r\n  width: 300px;\r\n  float:left;\r\n}\r\n.column tr td { padding: 15px; }\r\n.column-wrap { \r\n  padding:0!important; \r\n  margin:0 auto; \r\n  max-width:600px!important;\r\n}\r\n.column table { width:100%;}\r\n.social .column {\r\n  width: 280px;\r\n  min-width: 279px;\r\n  float:left;\r\n}\r\n\r\n/* Be sure to place a .clear element after each set of columns, just to be safe */\r\n.clear { display: block; clear: both; }\r\n\r\n\r\n/* ------------------------------------------- \r\n    PHONE\r\n    For clients that support media queries.\r\n    Nothing fancy. \r\n-------------------------------------------- */\r\n@media only screen and (max-width: 600px) {\r\n  \r\n  a[class="btn"] { display:block!important; margin-bottom:10px!important; background-image:none!important; margin-right:0!important;}\r\n\r\n  div[class="column"] { width: auto!important; float:none!important;}\r\n  \r\n  table.social div[class="column"] {\r\n    width:auto!important;\r\n  }\r\n\r\n}\r\n  -->\r\n  </style>  \r\n\r\n</head>\r\n \r\n<body bgcolor="#FFFFFF" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;-webkit-font-smoothing: antialiased;-webkit-text-size-adjust: none;height: 100%;width: 100%!important;">\r\n\r\n<!-- HEADER -->\r\n<table class="head-wrap" bgcolor="#ffffff" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;width: 100%;">\r\n  <tr style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n    <td style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;"></td>\r\n    <td class="header container" style="margin: 0 auto!important;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;display: block!important;max-width: 600px!important;clear: both!important;">\r\n        \r\n        <div class="content" style="margin: 0 auto;padding: 15px;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;max-width: 600px;display: block;">\r\n        <table bgcolor="#ffffff" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;width: 100%;">\r\n          <tr style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n            <td style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;"><!--img src="http://placehold.it/200x50/" /--></td>\r\n            <td align="right" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;"><h6 class="collapse" style="margin: 0!important;padding: 0;font-family: &quot;HelveticaNeue-Light&quot;, &quot;Helvetica Neue Light&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Lucida Grande&quot;, sans-serif;line-height: 1.1;margin-bottom: 15px;color: #444;font-weight: 900;font-size: 14px;text-transform: uppercase;">Gift Invitation</h6></td>\r\n          </tr>\r\n        </table>\r\n        </div>\r\n        \r\n    </td>\r\n    <td style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;"></td>\r\n  </tr>\r\n</table><!-- /HEADER -->\r\n\r\n\r\n<!-- BODY -->\r\n<table class="body-wrap" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;width: 100%;">\r\n  <tr style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n    <td style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;"></td>\r\n    <td class="container" bgcolor="#FFFFFF" style="margin: 0 auto!important;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;display: block!important;max-width: 600px!important;clear: both!important;">\r\n\r\n      <div class="content" style="margin: 0 auto;padding: 15px;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;max-width: 600px;display: block;">\r\n      <table style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;width: 100%;">\r\n        <tr style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n          <td style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;">\r\n            <h3 style="margin: 0;padding: 0;font-family: &quot;HelveticaNeue-Light&quot;, &quot;Helvetica Neue Light&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Lucida Grande&quot;, sans-serif;line-height: 1.1;margin-bottom: 15px;color: #000;font-weight: 500;font-size: 27px;">Hello [booking:field-first-name] and [booking:passenger-2-firstname],</h3>\r\n            <p class="lead" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;margin-bottom: 10px;font-weight: normal;font-size: 17px;line-height: 1.6;">Thank you for choosing to travel with Viking again. We hope you will have a pleasant journey, and to wish you "bon voyage" we invite you to select two gifts.\r\nIn order to receive your gifts please use the link below. Please note that the URL will expire after 21 days from the date of this email.</p>\r\n            <!-- Callout Panel -->\r\n            <p class="callout" style="margin: 0;padding: 15px;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;margin-bottom: 15px;font-weight: normal;font-size: 14px;line-height: 1.6;background-color: #ECF8FF;">\r\n              <a href="[booking:redeem-link]" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;color: #2BA6CB;font-weight: bold;">Redeem your gifts!</a>\r\n            </p><!-- /Callout Panel -->         \r\n                        \r\n            <!-- social & contact -->\r\n            <table class="social" width="100%" style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;, &quot;Helvetica&quot;, Helvetica, Arial, sans-serif;background-color: #ebebeb;width: 100%;">\r\n              <tr style="margin: 0;padding: 0;font-family: &quot;Helvetica Neue&quot;